mapper.update:
UPDATE MYPAGE_P_COLLECTION MPC
SET MPC.CLT_STATUS = 'N'
WHERE MPC.CLT_NO = #{ cltNo }

mapper.select:
SELECT NICKNAME
FROM MEMBER 
WHERE ID = #{ id }

SELECT MEMBER_NO
FROM MEMBER 
WHERE ID =  #{ id }

SELECT COUNT(*)
FROM FOLLOWER
WHERE TO_MEM_NO = #{ toMemNo }
    AND FROM_MEM_NO = #{ fromMemNo }

		SELECT COUNT(*)
		FROM FOLLOWING
		WHERE FROM_MEM_NO = #{ fromMemNo }
   			AND TO_MEM_NO = #{ toMemNo }
